import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchOrders, updateOrderStatus } from '../redux/actions';
import { motion, AnimatePresence } from 'framer-motion';

function AdminDashboard() {
  const dispatch = useDispatch();
  const { orders, loading, error } = useSelector(state => state.orders);
  const [isInitialLoad, setIsInitialLoad] = useState(true);
  
  useEffect(() => {
    dispatch(fetchOrders()).then(() => {
      setIsInitialLoad(false);
    });
    
    // Poll for updates every 10 seconds
    const interval = setInterval(() => {
      dispatch(fetchOrders());
    }, 10000);
    
    return () => clearInterval(interval);
  }, [dispatch]);
  
  const handleStatusChange = (orderId, newStatus) => {
    dispatch(updateOrderStatus(orderId, newStatus));
  };
  
  const getNextStatus = (currentStatus) => {
    switch (currentStatus) {
      case 'Pending': return 'Cooking';
      case 'Cooking': return 'Ready';
      case 'Ready': return 'Delivered';
      default: return null;
    }
  };
  
  const getStatusEmoji = (status) => {
    switch (status) {
      case 'Pending': return '⏱️';
      case 'Cooking': return '🔥';
      case 'Ready': return '✅';
      case 'Delivered': return '🚚';
      default: return '';
    }
  };
  
  const getButtonClass = (status) => {
    switch (status) {
      case 'Pending': return 'cooking-button';
      case 'Cooking': return 'ready-button';
      case 'Ready': return 'delivered-button';
      default: return '';
    }
  };
  
  const getButtonText = (status) => {
    switch (status) {
      case 'Pending': return 'Start Cooking';
      case 'Cooking': return 'Mark Ready';
      case 'Ready': return 'Mark Delivered';
      default: return '';
    }
  };
  
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };
  
  const calculateTimeDifference = (dateString) => {
    const orderDate = new Date(dateString);
    const now = new Date();
    const diffInMinutes = Math.floor((now - orderDate) / (1000 * 60));
    
    if (diffInMinutes < 60) {
      return `${diffInMinutes}m ago`;
    } else {
      const hours = Math.floor(diffInMinutes / 60);
      const mins = diffInMinutes % 60;
      return `${hours}h ${mins}m ago`;
    }
  };
  
  if (loading && orders.length === 0 && isInitialLoad) {
    return (
      <div className="loading-container">
        <motion.div 
          className="loading-spinner"
          animate={{ 
            rotate: 360,
            scale: [1, 1.2, 1]
          }}
          transition={{ 
            rotate: { duration: 1.5, repeat: Infinity, ease: "linear" },
            scale: { duration: 1, repeat: Infinity, ease: "easeInOut" }
          }}
        >
          <svg width="50" height="50" viewBox="0 0 50 50">
            <circle
              cx="25"
              cy="25"
              r="20"
              stroke="#4a90e2"
              strokeWidth="4"
              fill="none"
              strokeDasharray="94.2"
              strokeDashoffset="25"
            />
          </svg>
        </motion.div>
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          Loading orders...
        </motion.p>
      </div>
    );
  }
  
  if (error) {
    return (
      <motion.div 
        className="error-container"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="error-icon">❌</div>
        <h3>Error Loading Orders</h3>
        <p>{error}</p>
        <button 
          className="retry-button"
          onClick={() => dispatch(fetchOrders())}
        >
          Retry
        </button>
      </motion.div>
    );
  }
  
  // Group orders by status
  const pendingOrders = orders.filter(order => order.status === 'Pending');
  const cookingOrders = orders.filter(order => order.status === 'Cooking');
  const readyOrders = orders.filter(order => order.status === 'Ready');
  const deliveredOrders = orders.filter(order => order.status === 'Delivered');
  
  const columnVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { staggerChildren: 0.1 }
    }
  };
  
  const cardVariants = {
    hidden: { opacity: 0, scale: 0.9, y: 10 },
    visible: { 
      opacity: 1, 
      scale: 1, 
      y: 0,
      transition: { type: "spring", stiffness: 300, damping: 20 }
    },
    exit: { 
      opacity: 0, 
      scale: 0.8, 
      transition: { duration: 0.3 } 
    },
    hover: {
      y: -5,
      boxShadow: "0 10px 20px rgba(0,0,0,0.1)",
      transition: { type: "spring", stiffness: 400, damping: 10 }
    }
  };
  
  return (
    <div className="admin-dashboard">
      <motion.div 
        className="dashboard-header"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1>Restaurant Management Dashboard</h1>
        <div className="dashboard-stats">
          <div className="stat-box">
            <span className="stat-value">{orders.length}</span>
            <span className="stat-label">Total Orders</span>
          </div>
          <div className="stat-box pending-stat">
            <span className="stat-value">{pendingOrders.length}</span>
            <span className="stat-label">Pending</span>
          </div>
          <div className="stat-box cooking-stat">
            <span className="stat-value">{cookingOrders.length}</span>
            <span className="stat-label">Cooking</span>
          </div>
          <div className="stat-box ready-stat">
            <span className="stat-value">{readyOrders.length}</span>
            <span className="stat-label">Ready</span>
          </div>
        </div>
        <div className="last-updated">
          {loading ? (
            <motion.span
              animate={{ opacity: [1, 0.5, 1] }}
              transition={{ duration: 1.5, repeat: Infinity }}
            >
              Refreshing...
            </motion.span>
          ) : (
            <span>Last updated: {new Date().toLocaleTimeString()}</span>
          )}
        </div>
      </motion.div>
      
      <div className="order-columns">
        {['Pending', 'Cooking', 'Ready', 'Delivered'].map(status => {
          const statusOrders = {
            'Pending': pendingOrders,
            'Cooking': cookingOrders,
            'Ready': readyOrders,
            'Delivered': deliveredOrders
          }[status];
          
          const columnClass = `order-column ${status.toLowerCase()}`;
          
          return (
            <motion.div 
              key={status}
              className={columnClass}
              variants={columnVariants}
              initial="hidden"
              animate="visible"
              transition={{ 
                staggerChildren: 0.05,
                delayChildren: ['Pending', 'Cooking', 'Ready', 'Delivered'].indexOf(status) * 0.15
              }}
            >
              <div className="column-header">
                <h3>{getStatusEmoji(status)} {status}</h3>
                <div className="order-count">{statusOrders.length}</div>
              </div>
              
              <div className="column-content">
                <AnimatePresence>
                  {statusOrders.slice(0, status === 'Delivered' ? 5 : undefined).map(order => (
                    <motion.div 
                      key={order._id}
                      className="order-card"
                      variants={cardVariants}
                      initial="hidden"
                      animate="visible"
                      exit="exit"
                      whileHover="hover"
                      layout
                    >
                      <div className="order-card-header">
                        <span className="order-id">#{order._id.slice(-6)}</span>
                        <span className="order-time" title={formatDate(order.createdAt)}>
                          {calculateTimeDifference(order.createdAt)}
                        </span>
                      </div>
                      
                      <div className="customer-info">
                        <div className="customer-avatar">{order.customerName.charAt(0).toUpperCase()}</div>
                        <div className="customer-details">
                          <strong>{order.customerName}</strong>
                          {order.customerPhone && <span className="customer-phone">{order.customerPhone}</span>}
                        </div>
                      </div>
                      
                      {status !== 'Delivered' && (
                        <div className="order-items">
                          {order.items.map((item, index) => (
                            <div key={index} className="order-item">
                              <span className="item-quantity">{item.quantity}×</span>
                              <span className="item-name">{item.name}</span>
                            </div>
                          ))}
                        </div>
                      )}
                      
                      {status === 'Pending' && (
                        <div className="order-total">
                          <span className="total-label">Total:</span>
                          <span className="total-amount">${order.totalAmount.toFixed(2)}</span>
                        </div>
                      )}
                      
                      {status === 'Delivered' ? (
                        <div className="delivered-time">
                          Delivered {calculateTimeDifference(order.updatedAt)}
                        </div>
                      ) : (
                        <motion.button 
                          className={`status-button ${getButtonClass(order.status)}`}
                          onClick={() => handleStatusChange(order._id, getNextStatus(order.status))}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                        >
                          {getButtonText(order.status)}
                        </motion.button>
                      )}
                    </motion.div>
                  ))}
                </AnimatePresence>
                
                {status === 'Delivered' && deliveredOrders.length > 5 && (
                  <motion.div 
                    className="more-orders"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.5 }}
                  >
                    +{deliveredOrders.length - 5} more orders
                  </motion.div>
                )}
              </div>
            </motion.div>
          );
        })}
      </div>
    </div>
  );
}

export default AdminDashboard;
