import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchOrders, updateOrderStatus } from '../redux/actions';

function AdminDashboard() {
  const dispatch = useDispatch();
  const { orders, loading, error } = useSelector(state => state.orders);
  
  useEffect(() => {
    dispatch(fetchOrders());
    // Poll for updates every 10 seconds
    const interval = setInterval(() => {
      dispatch(fetchOrders());
    }, 10000);
    
    return () => clearInterval(interval);
  }, [dispatch]);
  
  const handleStatusChange = (orderId, newStatus) => {
    dispatch(updateOrderStatus(orderId, newStatus));
  };
  
  const getNextStatus = (currentStatus) => {
    switch (currentStatus) {
      case 'Pending': return 'Cooking';
      case 'Cooking': return 'Ready';
      case 'Ready': return 'Delivered';
      default: return null;
    }
  };
  
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };
  
  if (loading && orders.length === 0) return <div className="loading">Loading orders...</div>;
  if (error) return <div className="error">Error: {error}</div>;
  
  // Group orders by status
  const pendingOrders = orders.filter(order => order.status === 'Pending');
  const cookingOrders = orders.filter(order => order.status === 'Cooking');
  const readyOrders = orders.filter(order => order.status === 'Ready');
  const deliveredOrders = orders.filter(order => order.status === 'Delivered');
  
  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      
      <div className="order-columns">
        <div className="order-column pending">
          <h3>Pending ({pendingOrders.length})</h3>
          {pendingOrders.map(order => (
            <div key={order._id} className="admin-order-card">
              <div className="admin-order-header">
                <span className="order-id">#{order._id.slice(-6)}</span>
                <span className="order-time">{formatDate(order.createdAt)}</span>
              </div>
              
              <div className="customer-info">
                <strong>{order.customerName}</strong>
                {order.customerPhone && <span> • {order.customerPhone}</span>}
              </div>
              
              <div className="admin-order-items">
                {order.items.map((item, index) => (
                  <div key={index}>
                    {item.quantity}x {item.name}
                  </div>
                ))}
              </div>
              
              <div className="admin-order-total">
                Total: ${order.totalAmount.toFixed(2)}
              </div>
              
              <button 
                className="status-button cooking-button"
                onClick={() => handleStatusChange(order._id, getNextStatus(order.status))}
              >
                Start Cooking
              </button>
            </div>
          ))}
        </div>
        
        <div className="order-column cooking">
          <h3>Cooking ({cookingOrders.length})</h3>
          {cookingOrders.map(order => (
            <div key={order._id} className="admin-order-card">
              <div className="admin-order-header">
                <span className="order-id">#{order._id.slice(-6)}</span>
                <span className="order-time">{formatDate(order.createdAt)}</span>
              </div>
              
              <div className="customer-info">
                <strong>{order.customerName}</strong>
                {order.customerPhone && <span> • {order.customerPhone}</span>}
              </div>
              
              <div className="admin-order-items">
                {order.items.map((item, index) => (
                  <div key={index}>
                    {item.quantity}x {item.name}
                  </div>
                ))}
              </div>
              
              <button 
                className="status-button ready-button"
                onClick={() => handleStatusChange(order._id, getNextStatus(order.status))}
              >
                Mark Ready
              </button>
            </div>
          ))}
        </div>
        
        <div className="order-column ready">
          <h3>Ready ({readyOrders.length})</h3>
          {readyOrders.map(order => (
            <div key={order._id} className="admin-order-card">
              <div className="admin-order-header">
                <span className="order-id">#{order._id.slice(-6)}</span>
                <span className="order-time">{formatDate(order.createdAt)}</span>
              </div>
              
              <div className="customer-info">
                <strong>{order.customerName}</strong>
                {order.customerPhone && <span> • {order.customerPhone}</span>}
              </div>
              
              <button 
                className="status-button delivered-button"
                onClick={() => handleStatusChange(order._id, getNextStatus(order.status))}
              >
                Mark Delivered
              </button>
            </div>
          ))}
        </div>
        
        <div className="order-column delivered">
          <h3>Delivered ({deliveredOrders.length})</h3>
          {deliveredOrders.slice(0, 5).map(order => (
            <div key={order._id} className="admin-order-card">
              <div className="admin-order-header">
                <span className="order-id">#{order._id.slice(-6)}</span>
                <span className="order-time">{formatDate(order.createdAt)}</span>
              </div>
              
              <div className="customer-info">
                <strong>{order.customerName}</strong>
              </div>
              
              <div className="delivered-time">
                Delivered: {formatDate(order.updatedAt)}
              </div>
            </div>
          ))}
          {deliveredOrders.length > 5 && (
            <div className="more-orders">
              +{deliveredOrders.length - 5} more orders
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
